import React, { useState } from 'react';

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [rememberMe, setRememberMe] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();

    // Ici, vous pouvez appeler votre backend pour l'authentification
    // ex: login({ username, password, rememberMe })
    //   .then(...)
    //   .catch(...);

    console.log('Nom d’utilisateur:', username);
    console.log('Mot de passe:', password);
    console.log('Se souvenir de moi:', rememberMe);

    alert('Tentative de connexion...');
  };

  return (
    <div className="container my-5" style={{ maxWidth: '400px' }}>
      <h3 className="text-center mb-4">Content de vous revoir&nbsp;!</h3>
      <h4 className="text-center mb-4">Connectez-vous à votre compte</h4>

      <form onSubmit={handleSubmit}>
        {/* Nom d'utilisateur */}
        <div className="mb-3">
          <input
            type="text"
            className="form-control"
            placeholder="Entrez votre nom d’utilisateur"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>

        {/* Mot de passe */}
        <div className="mb-3">
          <input
            type="password"
            className="form-control"
            placeholder="Entrez votre mot de passe"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>

        {/* Se souvenir de moi */}
        <div className="form-check mb-3">
          <input
            className="form-check-input"
            type="checkbox"
            id="rememberMe"
            checked={rememberMe}
            onChange={(e) => setRememberMe(e.target.checked)}
          />
          <label className="form-check-label" htmlFor="rememberMe">
            Se souvenir de moi
          </label>
          {/* Lien "mot de passe oublié" */}
          <a href="/reset-password" className="ms-2">
            Vous avez oublié votre mot de passe&nbsp;?
          </a>
        </div>

        {/* Bouton Se connecter */}
        <button type="submit" className="btn btn-primary w-100 btn-se-connecter">
          Se connecter
        </button>
      </form>
    </div>
  );
}

export default Login;
