// frontend/src/pages/Activities.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Activities() {
  const [activities, setActivities] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5005/api/activities', {
      withCredentials: true,
      headers: { 'Cache-Control': 'no-cache' } // Désactiver le cache côté client
    })
    .then(res => {
      console.log("Activités récupérées :", res.data);
      setActivities(res.data);
    })
    .catch(err => console.error("Erreur lors du chargement des activités", err));
  }, []);

  return (
    <div className="container">
      <h1 className="mt-4">Activités Disponibles</h1>
      <ul className="list-group">
        {activities.map(activity => (
          <li key={activity.id} className="list-group-item">
            <h5>{activity.title}</h5>
            <p>Date : {activity.date}</p>
            <p>Heure : {activity.hour}</p>
            <p>Description : {activity.description}</p>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Activities;
